import { useHistory } from "react-router-dom"
import { useState } from "react"
import toast from "react-hot-toast"
import "./style.css"

const Home = ({user}) => {
    const history = useHistory()
    
    const [nota1, setNota1] = useState()
    const [nota2, setNota2] = useState()
    const [notaFinal, setNotaFinal] = useState()

    const logout = () => {

        localStorage.clear()
        history.push("/")
        toast.error("Logout Success")
    }

    const FinalGrade = () => {
       setNotaFinal ((parseFloat(nota1) * 0.4) + (parseFloat(nota2) * 0.6))
    }
    
    return(

        <div className="homePage">
            <header>
                <button className="logoutButton" onClick={() => logout()}>Logout</button>
            </header>
            <div className="inputNotas">
                <h1>Olá, {user.name}!</h1>
                <div>
                <p>Média das Atividades:</p><input type="number" onChange={(evt) => setNota1(evt.target.value)} value={nota1} onInput={e => setNotaFinal(nota1 * 0.4 + nota2 * 0.6)} e={notaFinal}/>
                <p>Nota da Prova:</p><input type="number" onChange={(evt) => setNota2(evt.target.value)} value={nota2} onInput={e => setNotaFinal(nota1 * 0.4 + nota2 * 0.6)} e={notaFinal}/>
                </div>
                {FinalGrade}
                <p>Média Final: <span>{notaFinal}</span></p>
            </div>
        </div>
    )
}

export default Home;

