import { useHistory } from "react-router-dom"
import { useState } from "react"
import toast from "react-hot-toast"
import "./style.css"

const Home = ({user}) => {
    const history = useHistory()
    
    const [nota1, setNota1] = useState()
    const [nota2, setNota2] = useState()
    const [notaFinal, setNotaFinal] = useState()

    const logout = () => {

        localStorage.clear()
        history.push("/")
        toast.error("Logout Success")
    }

    const FinalGrade = () => {

       const result = ((parseFloat(nota1) * 0.4) + (parseFloat(nota2) * 0.6))

       setNotaFinal(result.toFixed(1))
    }
    
    function aprovacao(media){

        if(media >= 6){
            document.getElementByID("mensagem").innerHTML = "Parabéns, você foi aprovado! Sua média final foi ";
        }

    }

    aprovacao(notaFinal);

    return(

        <div className="homePage">
            <header>
                <button className="logoutButton" onClick={() => logout()}>Logout</button>
            </header>
            <div className="inputNotas">
                <h1>Olá, {user.name}!</h1>
                <div onChange={FinalGrade}>
                <label class="labelNota" for="atividades">Atividades</label>
                <br/>
                <input id="atividades" class = "campoNota" type="number" min="0" max="10" label = "Nota de atividades" onChange={(evt) => setNota1(evt.target.value)} value={nota1}/>
                <br/>
                <br/>
                <label class="labelNota" for="prova">Prova</label>
                <br/>
                <input id="prova" class = "campoNota" type="number" min="0" max="10" label = "Nota da prova" onChange={(evt) => setNota2(evt.target.value)} value={nota2}/>
                </div>
                <br/>
                
                <p id="mensagem"><span onChange={aprovacao(notaFinal)} id="notaFinal">{notaFinal}</span></p>
                
            </div>
        </div>
    )
}



export default Home;

/*
<p>Média das Atividades:</p>
<p>Nota da Prova:</p>

                <button className="calculateButton" onClick={FinalGrade}>Calcular</button>
<p>Parabéns, você foi aprovado!</p>
<p>Não desista! Ainda dá pra recuperar!</p>
<p>Que pena, dessa vez sua nota não foi suficiente...</p>
*/
